INSERT INTO `fountain_pen` (`id`, `serial_number`, `brand_id`, `brand`, `name`, `price`, `nib_id`, `material`, `description`, `color`, `series`, `cap_type`, `inventory`, `image`, `created_at`, `valid`) VALUES
(1, '', 4, 'PILOT', 'PILOT Capless按鍵鋼筆', 7900.00, '3', '1', 'PILOT(CAPLESS)系列是世界首創的按鍵式鋼筆,1963年開使販售後,一直名列於暢銷榜上,CAPLESS系列如同原子筆一般,只要按鍵按壓一下,就能輕鬆書寫.', '3', 'Capless', '1', 0, 'PILOT_001-1.jpg', '0000-00-00 00:00:00', 1),
(2, '', 4, 'PILOT', 'PILOT Custom 845鋼筆', 12640.00, '3', '4', '在PILOT製作鋼筆的百年歷史中，誕生了「CUSTOM」系列。CUSTON」譯為「訂製的、容製的」。表示這支筆的由來就是為了要因應各種使用鋼筆的人。每個人的書寫方式都不同，喜好的筆尖粗細也不相同，故「CUSTOM」系列有最完整的筆尖與尺寸來對應不同的書寫方式', '1', 'Custom', '1', 0, 'PILOT_002-1.jpg', '0000-00-00 00:00:00', 1),
(3, '', 4, 'PILOT', 'PILOT Custom 845鋼筆', 12640.00, '2', '4', '在PILOT製作鋼筆的百年歷史中，誕生了「CUSTOM」系列。CUSTON」譯為「訂製的、容製的」。表示這支筆的由來就是為了要因應各種使用鋼筆的人。每個人的書寫方式都不同，喜好的筆尖粗細也不相同，故「CUSTOM」系列有最完整的筆尖與尺寸來對應不同的書寫方式', '1', 'Custom', '2', 0, 'PILOT_003-1.jpg', '0000-00-00 00:00:00', 1),
(4, '', 4, 'PILOT', 'PILOT Custom 845鋼筆', 12640.00, '2', '4', '在PILOT製作鋼筆的百年歷史中，誕生了「CUSTOM」系列。CUSTON」譯為「訂製的、容製的」。表示這支筆的由來就是為了要因應各種使用鋼筆的人。每個人的書寫方式都不同，喜好的筆尖粗細也不相同，故「CUSTOM」系列有最完整的筆尖與尺寸來對應不同的書寫方式', '5', 'Custom', '3', 0, 'PILOT_004-1.jpg', '0000-00-00 00:00:00', 1),
(5, '', 4, 'PILOT', 'PILOT Custom 742鋼筆', 5530.00, '3', '2', '採用14K金筆尖， 有著百年的製作經驗，古典派的鋼筆外觀，現代感的書寫筆觸。', '5', 'Custom', '4', 0, 'PILOT_005-1.jpg', '0000-00-00 00:00:00', 1),
(6, '', 4, 'PILOT', 'PILOT Custom 742鋼筆', 5530.00, '2', '2', '採用14K金筆尖， 有著百年的製作經驗，古典派的鋼筆外觀，現代感的書寫筆觸。', '1', 'Custom', '1', 0, 'PILOT_006-1.jpg', '0000-00-00 00:00:00', 1),
(7, '', 4, 'PILOT', 'PILOT Custom 742鋼筆', 5530.00, '2', '2', '採用14K金筆尖， 有著百年的製作經驗，古典派的鋼筆外觀，現代感的書寫筆觸。', '1', 'Custom', '2', 0, 'PILOT_007-1.jpg', '0000-00-00 00:00:00', 1),
(8, '', 4, 'PILOT', 'PILOT Capless按鍵鋼筆', 5925.00, '3', '7', 'PILOT(CAPLESS)系列是世界首創的按鍵式鋼筆,1963年開使販售後,一直名列於暢銷榜上,CAPLESS系列如同原子筆一般,只要按鍵按壓一下,就能輕鬆書寫.', '1', 'Capless', '3', 0, 'PILOT_008-1.jpg', '0000-00-00 00:00:00', 1),
(9, '', 4, 'PILOT', 'PILOT Capless按鍵鋼筆', 5925.00, '2', '7', 'PILOT(CAPLESS)系列是世界首創的按鍵式鋼筆,1963年開使販售後,一直名列於暢銷榜上,CAPLESS系列如同原子筆一般,只要按鍵按壓一下,就能輕鬆書寫.', '5', 'Capless', '3', 0, 'PILOT_009-1.jpg', '0000-00-00 00:00:00', 1),
(10, '', 4, 'PILOT', 'PILOT Custom 74鋼筆', 3555.00, '3', '2', 'CUSTOM系列以追求書寫流暢度為主，所以提供了多種風格的筆尖，讓每個人都可以選擇適合自己的筆尖，徜徉於書寫樂趣中；Custom有客製的意思，這就是該筆款追求的初衷。筆尖材質: 14K金 5號尖', '6', 'Custom', '2', 0, 'PILOT_010-1.jpg', '0000-00-00 00:00:00', 1),
(11, '', 4, 'PILOT', 'PILOT Elabo鋼筆', 5925.00, '1', '1', 'ELABO 彈性筆尖鋼筆1978年誕生，30年來數次改版，現行版的Elabo有著湯匙狀凸起的筆尖造型，可以表現出字幅的粗細強弱，日文漢字、假名書寫結構與中文相近，是最適合日文、中文書寫的鋼筆，軟彈的筆尖調性也受到英文書法族群的喜愛。標準配備是CON-40吸墨器，也可以使用卡式墨水。', '5', 'Elabo', '2', 0, 'PILOT_011-1.jpg', '0000-00-00 00:00:00', 1),
(12, '', 4, 'PILOT', 'PILOT Justus95鋼筆', 7900.00, '2', '2', '獨特的H(Hard writing)、 S(Soft writing)模式控制器， 可調節筆尖的彈力，一枝筆能夠同時擁有不同書寫風格。', '1', 'Justus', '4', 0, 'PILOT_012-1.jpg', '0000-00-00 00:00:00', 1),
(13, '', 4, 'PILOT', 'PILOT Custom 74鋼筆', 3555.00, '2', '2', 'CUSTOM系列以追求書寫流暢度為主，所以提供了多種風格的筆尖，讓每個人都可以選擇適合自己的筆尖，徜徉於書寫樂趣中；Custom有客製的意思，這就是該筆款追求的初衷。筆尖材質: 14K金 5號尖', '5', 'Custom', '4', 0, 'PILOT_013-1.jpg', '0000-00-00 00:00:00', 1),
(14, '', 4, 'PILOT', 'PILOT Lady White鋼筆', 4740.00, '3', '1', '是一款乾淨利落帶有高美感的灰白色鋼筆。筆身表面採用米色調和漆，營造出優雅的感覺和奢華。此外，筆身圖案又有櫻花和秋葉的區分，並以柔和的線條輪廓帶出整個筆身的特殊圖案，增加不少女性市場。', '20', 'Lady White', '4', 0, 'PILOT_014-1.jpg', '0000-00-00 00:00:00', 1),
(15, '', 4, 'PILOT', 'PILOT Elabo鋼筆', 5925.00, '3', '1', '1978年由PILOT與眾多鋼筆使用者共同開發，以書寫感受的最大化為目標而誕生的獨特設計。', '7', 'Elabo', '1', 0, 'PILOT_015-1.jpg', '0000-00-00 00:00:00', 1),
(16, '', 4, 'PILOT', 'PILOT MR2鋼筆', 553.00, '3', '1', '選用優雅的金屬色製作成筆桿顏色，讓表面富有光澤。', '2', 'MR', '1', 0, 'PILOT_016-1.jpg', '0000-00-00 00:00:00', 1),
(17, '', 4, 'PILOT', 'PILOT MR2鋼筆', 553.00, '3', '1', '選用優雅的金屬色製作成筆桿顏色，讓表面富有光澤。', '8', 'MR', '1', 0, 'PILOT_017-1.jpg', '0000-00-00 00:00:00', 1),
(18, '', 4, 'PILOT', 'PILOT MR2鋼筆', 553.00, '3', '1', '選用優雅的金屬色製作成筆桿顏色，讓表面富有光澤。', '4', 'MR', '1', 0, 'PILOT_018-1.jpg', '0000-00-00 00:00:00', 1),
(19, '', 4, 'PILOT', 'PILOT MR2鋼筆', 553.00, '3', '1', '選用優雅的金屬色製作成筆桿顏色，讓表面富有光澤。', '3', 'MR', '2', 0, 'PILOT_019-1.jpg', '0000-00-00 00:00:00', 1),
(20, '', 4, 'PILOT', 'PILOT探險家鋼筆', 474.00, '3', '2', '-筆身大膽色系,鮮豔繽紛且整體輕巧、書寫更加穩定 -擁有鋼筆、鋼珠筆兩種筆款讓消費者多種選擇 -每支筆另贈精緻三角筆盒,讓整體質感再提升,是最佳送禮首選 -多色卡水,簡單替芯', '5', '探險家', '2', 0, 'PILOT_020-1.jpg', '0000-00-00 00:00:00', 1),
(21, '', 4, 'PILOT', 'PILOT探險家鋼筆', 474.00, '3', '2', '-筆身大膽色系,鮮豔繽紛且整體輕巧、書寫更加穩定 -擁有鋼筆、鋼珠筆兩種筆款讓消費者多種選擇 -每支筆另贈精緻三角筆盒,讓整體質感再提升,是最佳送禮首選 -多色卡水,簡單替芯', '4', '探險家', '3', 0, 'PILOT_021-1.jpg', '0000-00-00 00:00:00', 1),
(22, '', 4, 'PILOT', 'PILOT Custom 88 Urushi鋼筆', 23700.00, '2', '2', '在PILOT製作鋼筆的百年歷史中,誕生了CUSTOM系列,CUSTOM譯為訂製的,客製的,表示這支筆的由來就是為了要因應各種使用鋼筆的人', '1', 'CUSTOM', '3', 0, 'PILOT_022-1.jpg', '0000-00-00 00:00:00', 1),
(23, '', 4, 'PILOT', 'PILOT微笑鋼筆', 300.00, '2', '2', '‧ 不繡鋼的筆尖上烙印微笑臉的圖像，讓書寫更添趣味。   ‧ 筆帽凸點貼心設計，便利使用者輕鬆開合筆帽。‧ 六角桿筆身讓筆置放桌面時不易滾動。 ‧ 符合人體工學的三面體握筆處，可讓手指正確 ‧ 端正的握拿，長時間舒寫不疲累。', '4', 'Custom', '3', 0, 'PILOT_023-1.jpg', '0000-00-00 00:00:00', 1),
(24, '', 4, 'PILOT', 'PILOT探險家鋼筆', 474.00, '3', '2', '-筆身大膽色系,鮮豔繽紛且整體輕巧、書寫更加穩定 -擁有鋼筆、鋼珠筆兩種筆款讓消費者多種選擇 -每支筆另贈精緻三角筆盒,讓整體質感再提升,是最佳送禮首選 -多色卡水,簡單替芯', '9', '探險家', '3', 0, 'PILOT_024-1.jpg', '0000-00-00 00:00:00', 1),
(25, '', 4, 'PILOT', 'PILOT探險家鋼筆', 474.00, '3', '2', '-筆身大膽色系,鮮豔繽紛且整體輕巧、書寫更加穩定 -擁有鋼筆、鋼珠筆兩種筆款讓消費者多種選擇 -每支筆另贈精緻三角筆盒,讓整體質感再提升,是最佳送禮首選 -多色卡水,簡單替芯', '10', '探險家', '3', 0, 'PILOT_025-1.jpg', '0000-00-00 00:00:00', 1),
(26, '', 4, 'PILOT', 'PILOT探險家鋼筆', 474.00, '3', '2', '-筆身大膽色系,鮮豔繽紛且整體輕巧、書寫更加穩定 -擁有鋼筆、鋼珠筆兩種筆款讓消費者多種選擇 -每支筆另贈精緻三角筆盒,讓整體質感再提升,是最佳送禮首選 -多色卡水,簡單替芯', '11', '探險家', '3', 0, 'PILOT_026-1.jpg', '0000-00-00 00:00:00', 1),
(27, '', 4, 'PILOT', 'PILOT探險家鋼筆', 474.00, '3', '2', '-筆身大膽色系,鮮豔繽紛且整體輕巧、書寫更加穩定 -擁有鋼筆、鋼珠筆兩種筆款讓消費者多種選擇 -每支筆另贈精緻三角筆盒,讓整體質感再提升,是最佳送禮首選 -多色卡水,簡單替芯', '12', '探險家', '3', 0, 'PILOT_027-1.jpg', '0000-00-00 00:00:00', 1),
(28, '', 4, 'PILOT', 'PILOT按鍵鋼筆', 4740.00, '3', '5', '亞光黑色沉穩的筆身。筆尖採用18K金，黃銅筆身，從商務到休閒使用，可以在廣泛的場景使用', '1', 'Custom', '2', 0, 'PILOT_028-1.jpg', '0000-00-00 00:00:00', 1),
(29, '', 4, 'PILOT', 'PILOT Capless按鍵鋼筆', 7900.00, '2', '1', 'PILOT(CAPLESS)系列是世界首創的按鍵式鋼筆,1963年開使販售後,一直名列於暢銷榜上,CAPLESS系列如同原子筆一般,只要按鍵按壓一下,就能輕鬆書寫.', '3', 'Capless', '2', 0, 'PILOT_029-1.jpg', '0000-00-00 00:00:00', 1),
(30, '', 4, 'PILOT', 'PILOT Custom NS鋼筆', 2765.00, '3', '2', '1971年以來發行的 CUSTOM系列鋼筆中出現了一個新系列。是此系列中首次使用不銹鋼筆的鋼筆，並作為此系列的入門型號，也是因應年輕族群採用了新的設計與配色。筆身與筆帽是中等大小的輪廓，並由一系列曲線組成。而筆夾延續了CUSTOM系列，並以新設計的帶有雨滴圖案，而這雨滴筆夾也是其特色之一。', '2', 'Custom', '4', 0, 'PILOT_030-1.jpg', '0000-00-00 00:00:00', 1),
(31, '', 4, 'PILOT', 'PILOT Custom NS鋼筆', 2765.00, '3', '2', '1971年以來發行的 CUSTOM系列鋼筆中出現了一個新系列。是此系列中首次使用不銹鋼筆的鋼筆，並作為此系列的入門型號，也是因應年輕族群採用了新的設計與配色。筆身與筆帽是中等大小的輪廓，並由一系列曲線組成。而筆夾延續了CUSTOM系列，並以新設計的帶有雨滴圖案，而這雨滴筆夾也是其特色之一。', '5', 'Custom', '4', 0, 'PILOT_031-1.jpg', '0000-00-00 00:00:00', 1),
(32, '', 4, 'PILOT', 'PILOT Custom NS鋼筆', 2765.00, '3', '2', '1971年以來發行的 CUSTOM系列鋼筆中出現了一個新系列。是此系列中首次使用不銹鋼筆的鋼筆，並作為此系列的入門型號，也是因應年輕族群採用了新的設計與配色。筆身與筆帽是中等大小的輪廓，並由一系列曲線組成。而筆夾延續了CUSTOM系列，並以新設計的帶有雨滴圖案，而這雨滴筆夾也是其特色之一。', '11', 'Custom', '4', 0, 'PILOT_032-1.jpg', '0000-00-00 00:00:00', 1),
(33, '', 4, 'PILOT', 'PILOT Custom 74鋼筆', 3555.00, '2', '2', 'CUSTOM系列以追求書寫流暢度為主，所以提供了多種風格的筆尖，讓每個人都可以選擇適合自己的筆尖，徜徉於書寫樂趣中；Custom有客製的意思，這就是該筆款追求的初衷。筆尖材質: 14K金 5號尖', '13', 'Custom', '4', 0, 'PILOT_033-1.jpg', '0000-00-00 00:00:00', 1),
(34, '', 4, 'PILOT', 'PILOT Elite E95s鋼筆', 2765.00, '2', '2', 'Elite 95S這是一種短尺寸的鋼筆，特別是在年輕一代中受到了極大的歡迎。筆身長度11.9cm是與襯衫前口袋深度相稱的短尺寸，相當攜帶方便，且筆身重量15g，放在襯衫口袋中也不會感到體重。筆身設計除了經典黑色外，另外添加了酒紅色跳脫傳統的印象，增加不少女性市場。', '1', 'Elite E95s', '4', 0, 'PILOT_034-1.jpg', '0000-00-00 00:00:00', 1),
(35, '', 4, 'PILOT', 'PILOT Elite E95s鋼筆', 2765.00, '2', '2', 'Elite 95S這是一種短尺寸的鋼筆，特別是在年輕一代中受到了極大的歡迎。筆身長度11.9cm是與襯衫前口袋深度相稱的短尺寸，相當攜帶方便，且筆身重量15g，放在襯衫口袋中也不會感到體重。筆身設計除了經典黑色外，另外添加了酒紅色跳脫傳統的印象，增加不少女性市場。', '5', 'Elite E95s', '1', 0, 'PILOT_035-1.jpg', '0000-00-00 00:00:00', 1),
(36, '', 3, 'montblanc', '萬寶龍大師傑作（大班）鍍玫瑰金鋼筆', 21500.00, '3', '2', '萬寶龍大師傑作（大班）系列鍍玫瑰金經典書寫工具是書寫歷史的設計。「Meisterstück」的意思是「傑作」，萬寶龍大師傑作（大班）系列自 1924 年推出而來，一直是優質奢華書寫文化的象徵。筆蓋和筆身以黑色珍貴樹脂製作，筆蓋頂部鑲嵌經典的白色萬寶龍標誌。 鋼筆配以手工製作 Au585/14K 實心金質鍍銠與鍍玫瑰金雙色筆尖。', '1', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_036-1.jpg', '0000-00-00 00:00:00', 1),
(37, '', 3, 'montblanc', '萬寶龍大師傑作（大班）鍍玫瑰金鋼筆', 21500.00, '2', '2', '萬寶龍大師傑作（大班）系列鍍玫瑰金經典書寫工具是書寫歷史的設計。「Meisterstück」的意思是「傑作」，萬寶龍大師傑作（大班）系列自 1924 年推出而來，一直是優質奢華書寫文化的象徵。筆蓋和筆身以黑色珍貴樹脂製作，筆蓋頂部鑲嵌經典的白色萬寶龍標誌。 鋼筆配以手工製作 Au585/14K 實心金質鍍銠與鍍玫瑰金雙色筆尖。', '1', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_037-1.jpg', '0000-00-00 00:00:00', 1),
(38, '', 3, 'montblanc', '萬寶龍大師傑作（大班）鍍金質鋼筆', 22000.00, '3', '2', '萬寶龍大師傑作（大班）經典鋼筆已成為獨特設計的經典。筆蓋和筆身以黑色珍貴樹脂製作，鑲嵌白色萬寶龍標誌。三個鍍鉑金圓環壓印萬寶龍字樣，鍍鉑金筆夾和手工製作金質筆尖，充分彰顯這款經典鋼筆的雋永設計。為能展現您的個性，萬寶龍提供免費鐫刻名字或姓名字首服務。在一個逐漸失卻個人風格的年代，我們的漆黑魅影、深海湛藍、勃根地紅、愛爾蘭綠、薰衣草紫、午夜藍調及豪情淡灰卡式墨水讓您能用這款經典鋼筆盡情發揮您的個人書寫風格。', '1', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_038-1.jpg', '0000-00-00 00:00:00', 1),
(39, '', 3, 'montblanc', '萬寶龍大師傑作（大班）鍍金質鋼筆', 22000.00, '2', '2', '萬寶龍大師傑作（大班）經典鋼筆已成為獨特設計的經典。筆蓋和筆身以黑色珍貴樹脂製作，鑲嵌白色萬寶龍標誌。三個鍍鉑金圓環壓印萬寶龍字樣，鍍鉑金筆夾和手工製作金質筆尖，充分彰顯這款經典鋼筆的雋永設計。為能展現您的個性，萬寶龍提供免費鐫刻名字或姓名字首服務。在一個逐漸失卻個人風格的年代，我們的漆黑魅影、深海湛藍、勃根地紅、愛爾蘭綠、薰衣草紫、午夜藍調及豪情淡灰卡式墨水讓您能用這款經典鋼筆盡情發揮您的個人書寫風格。', '1', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_039-1.jpg', '0000-00-00 00:00:00', 1),
(40, '', 3, 'montblanc', '萬寶龍大師傑作（大班）系列 GLACIER LEGRAND 藍色鋼筆', 28300.00, '3', '2', '萬寶龍大師傑作（大班）系列 Glacier LeGrand 鋼筆採用藍灰色的珍貴樹脂，靈感源自白朗峰的神秘冰川，代表萬寶龍對優質作品的全心奉獻，以及對探索冒險精神的專業態度。 此款書寫工具以手工製作的 Au585 / 14K 實心金質鍍銠筆尖製成，飾有躺臥在白朗峰山頂的蒙特維冰河「奇龍」。', '11', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_040-1.jpg', '0000-00-00 00:00:00', 1),
(41, '', 3, 'montblanc', '萬寶龍大師傑作（大班）系列 GLACIER LEGRAND 藍色鋼筆', 28300.00, '2', '2', '萬寶龍大師傑作（大班）系列 Glacier LeGrand 鋼筆採用藍灰色的珍貴樹脂，靈感源自白朗峰的神秘冰川，代表萬寶龍對優質作品的全心奉獻，以及對探索冒險精神的專業態度。 此款書寫工具以手工製作的 Au585 / 14K 實心金質鍍銠筆尖製成，飾有躺臥在白朗峰山頂的蒙特維冰河「奇龍」。', '11', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_041-1.jpg', '0000-00-00 00:00:00', 1),
(42, '', 3, 'montblanc', '大師傑作（大班）DOUÉ 暮藍系列鋼筆', 40900.00, '3', '2', '大師傑作（大班）Doué 暮藍系列是一款珍貴的藍色亮漆 Solitaire 產品，綴以鍍鉑金配件。白天後入夜前的神奇時刻——暮藍——營造出流暢的反差與令人屏息的氛圍，這正是此筆款的靈感來源。手工製作鍍銠金筆尖及筆身的六角形圖案，喻意大都會的燈光折射與夜間流竄的車輛。', '11', '萬寶龍大師傑作（大班）', '2', 0, 'montblanc_042-1.jpg', '0000-00-00 00:00:00', 1),
