INSERT INTO `teacher` (`id`, `name`, `about`, `image`, `valid`) VALUES
(1, '張雅琳', '書法大師，教學20年，擅長教導優雅鋼筆字。', 'teacher_4.jpg', 1),
(2, '陳俊宇', '藝術家，鋼筆書法教學，啟發創意風格。', 'teacher_5.jpg', 1),
(3, '蔡佳慧', '書法專家，鋼筆字教學，深受學生喜愛。', 'teacher_6.jpg', 1),
(4, '林志成', '鋼筆書法老師，教學風趣有趣。', 'teacher_7.jpg', 1),
(5, '王婉婷', '資深書法家，精通鋼筆技法，獨特風格。', 'teacher_8.jpg', 1),
(6, '吳浩然', '教學經驗豐富，專攻鋼筆書法之美。', 'teacher_9.jpg', 1),
(7, '林欣怡', '藝術家兼鋼筆老師，深受學生推崇。', 'teacher_10.jpg', 1),
(8, '黃偉文', '書法大家，專攻鋼筆字寫作技術。', 'teacher_11.jpg', 1),
(9, '許嘉玲', '藝術教育家，教學深入淺出，激發學生創意。', 'teacher_12.jpg', 1),
(10, '李宗翰', '鋼筆書法專業老師，激發學生藝術天分。', 'teacher_13.jpg', 1),
(11, '溫欣怡', '鋼筆書法導師，專業教學，注重基礎訓練。', 'teacher_14.jpg', 1),
(12, '林凱文', '藝術家，鋼筆書法教學，注重實踐應用。', 'teacher_15.jpg', 1),
(13, '鄭雅婷', '書法教育家，引導學生體驗鋼筆之美。', 'teacher_16.jpg', 1),
(14, '賴建宏', '專業書法老師，擅長培養學生獨特風格。', 'teacher_17.jpg', 1),
(15, '林怡君', '書法家兼鋼筆老師，融合東西方書法之美。', 'teacher_18.jpg', 1),
(16, '陳宇軒', '擅長將鋼筆融入印象派風格，以豐富的色彩和筆觸展現繪畫之美。', 'teacher_19.jpg', 1),
(17, '張婉如', '專攻楷書風格，以中國傳統書法為基礎，指導學生建立優雅書寫基礎。', 'teacher_20.jpg', 1),
(18, '王俊杰', '致力於創意書寫藝術，鼓勵學生將文字與繪畫結合，創造獨特之作品。', 'teacher_21.jpg', 1),
(19, '林雅琳', '深耕靜物畫領域，以寧靜的風格教授學生捕捉事物細節之美。', 'teacher_22.jpg', 1),
(20, '黃翰宇', '不僅是知名藝人，也是繪畫愛好者，專精於色彩運用，教學充滿活力。', 'teacher_23.jpg', 1),
(21, '蔡佳怡', '經驗豐富的漫畫家，以幽默風趣的教學風格深受學生喜愛。', 'teacher_24.jpg', 1),
(22, '李宗翰', '曾在畫廊展覽多次，以獨特視角指導學生捕捉動態場景的美。', 'teacher_25.jpg', 1),
(23, '鄭雨萱', '專攻抽象藝術，致力於啟發學生挑戰傳統，探索個人風格。', 'teacher_26.jpg', 1),
(24, '溫志明', '擁有卓越的素描技巧，教導學生細緻線條的表現。', 'teacher_27.jpg', 1),
(25, '賴美玲', '注重學生個性化指導，專注於人物繪畫，擁有多年教學經驗。', 'teacher_28.jpg', 1),
(26, '吳仁愛', '以獨特的卡通風格啟發學生創意，培養卓越繪畫技巧。', 'teacher_29.jpg', 1),
(27, '林偉文', '曾於美術館任職，擁有卓越的素描技巧，教導學生細緻線條的表現。', 'teacher_30.jpg', 1),
(28, '許怡君', '極富耐心的老師，教學風格溫馨，致力於引導學生發現色彩之美。', 'teacher_31.jpg', 1),
(29, '楊宏達', '插畫家兼鋼筆藝術家，以獨特的卡通風格啟發學生創意，培養卓越繪畫技巧。', 'teacher_32.jpg', 1),
(30, '賴欣怡', '曾於藝術學院任教，擅長鋼筆水彩風景畫，將大自然之美融入繪畫。', 'teacher_33.jpg', 1);
